# tables#utility tables# will be a single rowDROP TABLE IF EXISTS badwords;CREATE TABLE badwords (    id INTEGER PRIMARY KEY AUTO_INCREMENT,    kill_words TEXT,    warn_words TEXT,    updated_datetime TIMESTAMP DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP);DROP TABLE IF EXISTS images;CREATE TABLE `images` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `app` varchar(255) NOT NULL,  `mirrored` tinyint(1) DEFAULT '0',  `created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  KEY `app` (`app`),  KEY `mirrored` (`mirrored`));#locationdrop table if exists location;create table location (    location_id int not NULL    ,name varchar(50) not NULL    ,lat decimal(9,6) not NULL    ,lon decimal(9,6) not NULL    ,borough varchar(50) null    ,address varchar(100) null    ,city varchar(50) null    ,state char(2) null    ,primary key (location_id));#usersdrop table if exists user;create table user (    user_id int not null auto_increment    ,user_key varchar(10) not null    ,email varchar(100) not NULL    ,password varchar(255) not NULL    ,salt varchar(255) not NULL    ,phone varchar(10) NULL    ,first_name varchar(50) NULL    ,last_name varchar(50) NULL    ,image_id int NULL    ,location_id int null    ,is_active bool not null default 1    ,created_datetime timestamp not null default '0000-00-00 00:00:00'    ,updated_datetime timestamp default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP    ,primary key (user_id));drop table if exists user_group;create table user_group (    user_group_id int not null    ,group_name varchar(50) not NULL    ,description varchar(255) NULL    ,is_active bool not null default 1    ,created_datetime timestamp default CURRENT_TIMESTAMP    ,primary key (user_group_id));drop table if exists user__user_group;create table user__user_group (    user_id int not NULL    ,user_group_id int not NULL);#ideasdrop table if exists idea;create table idea (    idea_id int not null auto_increment    ,idea varchar(255) not NULL    ,submission_type enum('web','sms','email') not NULL    ,user_id int null    ,email varchar(100) null    ,phone varchar(10) null    ,first_name varchar(50) NULL    ,last_name varchar(50) NULL    ,num_flags smallint not null default 0    ,is_active bool not null default 1    ,created_datetime timestamp default CURRENT_TIMESTAMP    ,primary key (idea_id)    );#projectsdrop table if exists project;create table project (    project_id int not null auto_increment    ,title varchar(100) not null    ,description varchar(255) null    ,image_id int NULL    ,location_id int null    ,keywords text null    ,is_active bool not null default 1    ,created_datetime timestamp not null default '0000-00-00 00:00:00'    ,updated_datetime timestamp default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP    ,primary key (project_id)     );drop table if exists project__user;create table project__user (    project_id int not NULL    ,user_id int not NULL    ,is_project_admin bool not null default 0    ,created_datetime timestamp not null default CURRENT_TIMESTAMP        ,primary key (project_id, user_id));drop table if exists project__idea;create table project__idea (    project_id int not NULL    ,idea_id int not NULL    ,created_datetime timestamp not null default CURRENT_TIMESTAMP        ,primary key (project_id, idea_id));drop table if exists project__project_resource;create table project__project_resource (    project_id int not NULL    ,project_resource_id int not NULL    ,primary key (project_id, project_resource_id)    ,created_datetime timestamp not null default CURRENT_TIMESTAMP    );drop table if exists project_resource;create table project_resource (    project_resource_id int not null auto_increment    ,title varchar(100) not null    ,description varchar(255) null    ,url varchar(255) null    ,twitter_url varchar(255) NULL    ,facebook_url varchar(255) null    ,contact_name varchar(255) NULL    ,contact_email varchar(100) null    ,image_id int NULL    ,location_id int null    ,keywords text null    ,is_active bool not null default 1    ,created_datetime timestamp not null default '0000-00-00 00:00:00'    ,updated_datetime timestamp default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP    ,primary key (project_resource_id));drop table if exists project__project_resource;create table project__project_resource (    project_id int not NULL    ,project_resource_id int not NULL    ,created_datetime timestamp not null default CURRENT_TIMESTAMP        ,primary key (project_id, project_resource_id));drop table if exists project_link;create table project_link (    project_link_id int not null auto_increment    ,project_id int not null    ,title varchar(100) not null    ,url varchar(255) not null    ,is_active bool not null default 1    ,created_datetime timestamp not null default CURRENT_TIMESTAMP    ,primary key (project_link_id)    );drop table if exists project_goal;create table project_goal (    project_goal_id int not null auto_increment    ,project_id int not null    ,description varchar(255) not NULL    ,time_frame_numeric int NULL    ,time_frame_unit enum('day','week','month') NULL    ,is_accomplished bool not null default 0    ,is_active bool not null default 1    ,created_datetime timestamp not null default '0000-00-00 00:00:00'    ,updated_datetime timestamp default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP    ,primary key (project_goal_id)    );